import React from "react";
import { useState, useMemo, useEffect } from "react";
import "../App.css";
import mondaySdk from "monday-sdk-js";
import "monday-ui-react-core/dist/main.css";
import { Dropdown, Button } from "monday-ui-react-core";
import Nango from "@nangohq/frontend";
import {
  FacebookService,
  HTTPAuthorizationCredentials,
  User,
  UsersService,
  MondayService,
  QueryData,
  ColumnData,
  MondayItem,
} from "../api";

const monday = mondaySdk();

interface Board {
  id: string;
  name: string;
}

interface Option {
  value: string;
  label: string;
}

interface BoardColumn {
  id: string;
  title: string;
  type: string;
}

export const Connector = () => {
  const [connector, setConnector] = useState();
  const [connected, setConnected] = useState(false);
  const [user, setUser] = useState<User>();
  const [accountOptions, setAccountOptions] = useState<object[]>([]);
  const [selectedAccount, setSelectedAccount] = useState<object>();
  const [fields, setFields] = useState<object[]>([]);
  const [selectedFields, setSelectedFields] = useState<object[]>([]);
  const [boards, setBoards] = useState<Option[]>([]);
  const [selectedBoardOption, setSelectedBoardOption] = useState<Option>();
  const [boardColumns, setBoardColumns] = useState<Option[]>([]);
  const [selectedColumnOption, setSelectedColumnOption] = useState<Option>();
  const [columnType, setColumnType] = useState<Option>();
  const [date, setDate] = useState<Option>();

  const getIconUrl = (imgPath: string) => {
    return require(`../Static/images/${imgPath}.png`);
  };

  // const handleRunClick = () => {
  //   if (user?.monday_token && selectedBoardOption && selectedColumnOption) {
  //   MondayService.mondayItems(user.monday_token, selectedBoardOption?.value, selectedColumnOption?.value).then(
  //     (items: MondayItem[]) => {
  //       const queryData: QueryData = {
  //         monday_items: items,
  //         account_id: selectedAccount?.id,
  //         metrics: fields.map((field) => field.value),
  //         tyoe: columnType?.value,
  //         user
  //       }
  //       FacebookService.facebookFetchData(queryData).then(data: ColumnData[] => {
  //         MondayService.mondayAddData(user.monday_token, selectedBoardOption.value, data)
  //       })
  //     }
  //   );
  //   }
  // }

  const handleFieldSelect = (selectedField: Option) => {
    setSelectedFields((prevSelectedFields) => {
      // Check if the option is already selected
      if (prevSelectedFields.some((field) => field === selectedField)) {
        // If it is, remove it from the selection
        return prevSelectedFields.filter((field) => field !== selectedField);
      } else {
        // If it is not, add it to the selection
        return [...prevSelectedFields, selectedField];
      }
    });
  };

  const options = useMemo(
    () => [
      {
        value: "facebook",
        label: "Facebook",
        leftAvatar: getIconUrl("facebook-icon"),
      },
      {
        value: "instagram",
        label: "Instagram",
        leftAvatar: getIconUrl("instagram-icon"),
      },
      {
        value: "google",
        label: "Google Ads",
        leftAvatar: getIconUrl("google-ads-icon"),
      },
      {
        value: "google-analytics",
        label: "Google Analytics",
        leftAvatar: getIconUrl("google-analytics-icon"),
      },
    ],
    []
  );

  const dateOptions = useMemo(
    () => [
      { value: 1, label: "Last 1 Days" },
      { value: 7, label: "Last 7 Days" },
      { value: 30, label: "Last 30 Days" },
      { value: 90, label: "Last 90 Days" },
      { value: 365, label: "Last 365 Days" },
    ],
    []
  );

  const typeOptions = useMemo(
    () => [
      { value: "date", label: "Date" },
      { value: "link", label: "Link" },
      { value: "text", label: "Text" },
    ],
    []
  );

  async function getSessionToken() {
    return (await monday.get("sessionToken")).data;
  }

  async function updateUser(connectionId: string) {
    const sessionToken = await getSessionToken();

    const requestBody: HTTPAuthorizationCredentials = {
      scheme: "Bearer",
      credentials: sessionToken,
    };

    FacebookService.facebookLogin(connectionId, requestBody).then(
      (user: User) => {
        setUser(user);
      }
    );
  }

  const nango = new Nango({
    publicKey: "7b73b776-3a10-41c0-a5ee-feef16d372d4",
  });

  const connect = () => {
    if (connector === "facebook") {
      nango
        .auth("facebook", "test-connection-id", {
          authorization_params: { config_id: "728465868571401" },
        })
        .then((result) => {
          updateUser(result.connectionId);
          // save id to the database.
        })
        .catch((err) => {
          console.log(err);
        });
    }
  };

  useEffect(() => {
    const fetchUser = async () => {
      await getSessionToken().then((sessionToken) => {
        if (sessionToken) {
          UsersService.usersReadUserByMondaySession(sessionToken).then(
            (user: User) => {
              console.log(user);
              setUser(user);
            }
          );
        }
      });
    };
    fetchUser();
  }, []);

  useEffect(() => {
    if (connector == "facebook" && user?.facebook_token) {
      setConnected(true);
    } else {
      setConnected(false);
    }
  }, [connector]);

  useEffect(() => {
    if (connected) {
      if (connector === "facebook" && user?.facebook_token) {
        FacebookService.facebookAdAccounts(user.facebook_token).then(
          (accounts) => {
            setAccountOptions(accounts);
          }
        );
        FacebookService.facebookFields().then((fields) => {
          console.log(fields);
          setFields(fields);
        });
      }
    }
  }, [connected, user]);

  useEffect(() => {
    if (user?.monday_token) {
      MondayService.mondayBoards(user.monday_token).then((boards: Board[]) => {
        const boardOptions: Option[] = boards.map((board: Board) => ({
          value: board.id,
          label: board.name,
        }));
        setBoards(boardOptions);
      });
    }
  }, [user]);

  useEffect(() => {
    if (selectedBoardOption && user?.monday_token && columnType) {
      MondayService.mondayBoardColumns(
        selectedBoardOption.value,
        user.monday_token,
        columnType.value
      ).then((columns: BoardColumn[]) => {
        const columnOptions: Option[] = columns.map((column: BoardColumn) => ({
          value: column.id,
          label: column.title,
        }));
        setBoardColumns(columnOptions);
      });
    }
  }, [selectedBoardOption, columnType]);

  return (
    <div className="p-2">
      <div className="border-2 border-gray rounded-md p-5 mb-2">
        <p className="font-bold text-gray-500 text-sm">* Application</p>
        <Dropdown
          placeholder="Select an application"
          options={options}
          onOptionSelect={(e: any) => setConnector(e.value)}
        />
        {connected === false && (
          <Button onClick={() => connect()} className="mt-2">
            Connect
          </Button>
        )}
      </div>
      {connected && accountOptions && (
        <>
          <div className="border-2 border-grey rounded-md p-5 mb-2">
            <p className="font-bold text-gray-500 text-sm">* Account</p>
            <Dropdown
              placeholder="Select an account"
              className="mb-2"
              options={accountOptions}
              onOptionSelect={(e: Option) => setSelectedAccount(e)}
            />
            <p className="font-bold text-gray-500 text-sm">* Fields</p>
            <Dropdown
              placeholder="Select fields"
              multi
              multiline
              options={fields}
              onOptionSelect={(e: Option) => handleFieldSelect(e)}
            />
          </div>
          <div className="border-2 border-gray rounded-md p-5">
            <p className="font-bold text-gray-500 text-sm">* Board</p>
            <Dropdown
              options={boards}
              placeholder="Select a board"
              className="mb-2"
              onOptionSelect={(e: Option) => setSelectedBoardOption(e)}
            />
            <p className="font-bold text-gray-500 text-sm">* Column Type</p>
            <Dropdown
              placeholder="Date or Post Url"
              options={typeOptions}
              className="mb-2"
              onOptionSelect={(e: Option) => setColumnType(e)}
            />
            {columnType?.value === "link" || columnType?.value === "text" ? (
              <>
                <p className="font-bold text-gray-500 text-sm">* Column</p>
                <Dropdown
                  options={boardColumns}
                  onOptionSelect={(e: Option) => setSelectedColumnOption(e)}
                  placeholder="Select column"
                  className="mb-2"
                />
                <p className="font-bold text-gray-500 text-sm">* Date</p>
                <Dropdown
                  placeholder="Select a date range"
                  options={dateOptions}
                  className="mb-2"
                  onOptionSelect={(e: Option) => setDate(e)}
                />
              </>
            ) : columnType?.value === "date" ? (
              <>
                <p className="font-bold text-gray-500 text-sm">* Date Column</p>
                <Dropdown
                  options={boardColumns}
                  onOptionSelect={(e: Option) => setSelectedColumnOption(e)}
                  placeholder="Select column"
                  className="mb-2"
                />
              </>
            ) : null}
          </div>
          <Button
            // onClick={handleRunClick}
            className="mt-2"
          >
            Run
          </Button>
        </>
      )}
    </div>
  );
};
