import React from "react";
import { useState, useMemo} from "react";
import "../App.css";
import mondaySdk from "monday-sdk-js";
import "monday-ui-react-core/dist/main.css";
import { Dropdown, Button } from "monday-ui-react-core";
import Nango from '@nangohq/frontend';



// Usage of mondaySDK example, for more information visit here: https://developer.monday.com/apps/docs/introduction-to-the-sdk/
const monday = mondaySdk();

export const Connector = () => {

  const [connector, setConnector] = useState();


  const getIconUrl = (imgPath) => {
    return require(`../Static/images/${imgPath}.png`);
  };

  const options = useMemo(
    () => [
      {
        value: "facebook",
        label: "Facebook",
        leftAvatar: getIconUrl("facebook-icon"),
      },
      {
        value: "instagram",
        label: "Instagram",
        leftAvatar: getIconUrl("instagram-icon"),
      },
      {
        value: "google",
        label: "Google Ads",
        leftAvatar: getIconUrl("google-ads-icon"),
      },
      {
        value: "google-analytics",
        label: "Google Analytics",
        leftAvatar: getIconUrl("google-analytics-icon"),
      },
    ],
    []
  );

  const nango = new Nango({ publicKey: '7b73b776-3a10-41c0-a5ee-feef16d372d4' });

  const connect = () => {
      if (connector === "facebook") {
      nango.auth('facebook', 'test-connection-id', { authorization_params: { config_id: '728465868571401' }})
      .then((result) => {
        console.log(result);
      }).catch((err) => {
        console.log(err)
      });
    }
  }

  return  (
      <>
        <Dropdown
          ariaLabel="Hello"
          placeholder="Select an application"
          options={options}
          className="dropdown-stories-styles_with-chips !text-blue-500"
          onClick={(e) => setConnector(e.target.value)}
        />
        <Button onClick={() => connect()} className="mt-2">
          Connect
        </Button>
      </>
      );
    };
